import { Router } from "express";
import { AuthMiddleware } from "../auth/middleware/auth.middleware.js";
import { UserController } from "../controller/auth.controller.js";
import { UserMiddleware } from "../auth/middleware/user.meddleware.js";
export const userRouter = Router();
const authMiddleware = new AuthMiddleware();
const userMiddleware = new UserMiddleware();
const userController = new UserController();
userRouter.post('/signup', authMiddleware.signUp, userMiddleware.getUserByEmail, userController.signUp);
userRouter.post('/signin', userController.signIn);
